import React, { useEffect, useState } from 'react'
import "../Style/users.css"

const Users = () => {
  let[user,setUser]=useState([])
 
    let fetchUser=async()=>{
      let userData=await fetch('http://localhost:4000/users')
      .then(resp=>resp.json())
      SetUser(userData)
    }
    fetchUser()
   
 
  
  
  
  return (
    <>
    <div className="users">
      <table border={1}>
      <thead>
          <tr>
            <th>Id</th>
            <th>FirstName</th>
            <th>Last Name</th>
            <th>Email-Id</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Date of Birth</th>
            <th>Delete User</th>
          </tr>
        </thead>
        {user.map((e)=>{
        let{id,firstname,lastname,email,phone,address,birthDate}=e
        let deleteUser=()=>{
          let bool=window.confirm(`Do you want to delete ${firstname} user..?`)
          if(bool)
            {
              fetch(`http://localhost:4000/users/${id}`,{method:"DELETE"})
             alert(`${firstname} user is deleted`)

            }
            else{
              alert("user is Not deleted")
            }
          
        }
        return(
         
           <tbody>
              <tr>
                <td>{id}</td>
                <td>{firstname}</td>
                <td>{lastname}</td>
                <td>{email}</td>
                <td>{phone}</td>
                <td>{address}</td>
                <td>{birthDate}</td>
                <td>
                  <button onClick={deleteUser} className='user-btn'>Delete User</button>
                </td>
              </tr>
            </tbody>
         

        )
})}

      </table>
     
    </div>
    
    </>
  )
}

export default Users
