import React, { useState } from 'react'
import "../Style/Books.css"

const Books = () => {
 let[books,setBooks]= useState([])



  let fetchApi=async()=>{
    let apiData=await fetch('http://localhost:4000/books')
    .then(resp=>resp.json())
    setBooks(apiData)
  }
  fetchApi()
  // console.log(books)
  return (
   <>
   <div className="books">
    {
      books.map((elem)=>{
        let{title,authors,categories,pageCount,thumbnailUrl,isbn}=elem
       
        return(
          <>
          <div className="container">
           <div className="img-side">
            <img src={thumbnailUrl} />

           </div>
           <div className="content-side">
            <h4>{title}</h4>
            <div><b>Author:</b> {authors}</div>
            <div><b>Categories: </b>{categories}</div>
            <div><b>RegNo:</b> {isbn}</div>
            <div><b>Page Count:</b> {pageCount}</div>

           </div>

          </div>
          </>
        )
        
      })
    }

   </div>
   
   </>
  )
}

export default Books
