import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom'

const Cart = () => {
    let Cart=()=>{
        let[cart,setCart]=useState([])
        let navigate=useNavigate()
        useEffect(()=>{
            let fetchApi= async()=>{
                let apiData= await fetch(`http://localhost:4000/cart`)
                .then(resp=>resp.json())
                setCart(apiData)
            }

        },[cart])
    }
    let viewButton=(id)=>{
        navigate(`/userportal/readbooks/${id}`)
    }
    let visitBooks=()=>{
        navigate(`/userportal/books`)
    }
    let deleteCart=(id,cartid,carttitle)=>{
        fetch(`http://localhost:4000/cart/${id}`,
            {method:"DELETE"}
        )
        alert(`${carttitle} removed from cart`)
    }







 if(cart.length!=0)
    {
        return (
            <>
            <div className="carddata">
                <div className="header">My Cart</div>
                <table>
                    <thead>
                        <th>ID</th>
                        <th>Book Name</th>
                        <th>Image</th>
                        <th>Author</th>
                        <th>View Cart</th>
                        <th>Delete Cart</th>
                    </thead>
                </table>
            </div>
            
            </>
          )
    }
}

export default Cart
