import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom' 
import emptycart from '../Images/Empty_Shopping_Cart_blog.webp'

const Cart = () => {
    const [cart, setCart] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchApi = async () => {
            const apiData = await fetch(`http://localhost:4000/cart`)
                .then(resp => resp.json());
            setCart(apiData);
        };
        fetchApi();
    }, []); // Only run once when the component mounts

    const viewButton = (id) => {
        navigate(`/userportal/readbooks/${id}`);
    };

    const visitBooks = () => {
        navigate(`/userportal/books`);
    };

    const deleteCart = (id, carttitle) => {
        fetch(`http://localhost:4000/cart/${id}`, {
            method: "DELETE"
        });
        alert(`${carttitle} removed from cart`);
    };

    const addItemToCart = (item) => {
        fetch('http://localhost:4000/cart', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(item)
        })
        .then(resp => resp.json())
        .then(newItem => {
            setCart([...cart, newItem]); // Update cart with the new item
        });
    };

    // Assuming you have a form or button to add items to the cart
    const handleAddItem = () => {
        const newItem = {
            id: Date.now(), // Unique ID for the item
            cartid: '123', // Example cartid
            carttitle: 'New Book', // Example title
            cartauthor: 'Author Name', // Example author
            cartimage: 'https://example.com/image.jpg' // Example image URL
        };
        addItemToCart(newItem);
    };

    if (cart.length !== 0) {
        return (
            <>
                <div className="carddata">
                    <div className="header">My Cart</div>
                    <table>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Book Name</th>
                                <th>Image</th>
                                <th>Author</th>
                                <th className='viewcart'>View Cart</th>
                                <th className='deletecart'>Delete Cart</th>
                            </tr>
                        </thead>
                        <tbody>
                            {cart.map((elem) => {
                                let { id, cartid, carttitle, cartauthor, cartimage } = elem;
                                return (
                                    <tr key={id}>
                                        <td>{cartid}</td>
                                        <td>{carttitle}</td>
                                        <td><img src={cartimage} alt={carttitle} /></td>
                                        <td>{cartauthor}</td>
                                        <td className='viewcart'><button onClick={() => viewButton(cartid)} className='vc-btn'>View Cart</button></td>
                                        <td className='removecart'><button onClick={() => deleteCart(id, carttitle)} className='dc-btn'>Delete Cart</button></td>
                                    </tr>
                                );
                            })}
                        </tbody>
                    </table>
                </div>
                <div>
                    <button onClick={handleAddItem}>Add Item to Cart</button> {/* Example button to add item */}
                </div>
            </>
        );
    } else {
        return (
            <>
                <div className="cartcontainer">
                    <div className="cartdisplay"><img src={emptycart} alt="Empty Cart" /></div>
                    <div className="cartnavbutton"><button onClick={visitBooks}>Visit Books</button></div>
                </div>
            </>
        );
    }
};

export default Cart;
