import React, { useState } from 'react'
import { useParams } from 'react-router-dom'
import '../Style/'

const ReadBooks = () => {
    let params=useParams()
    let bookId=params.id
    let[book,setBook]=useState({})
    let fetchBookApi=async()=>{
       let bookData=await fetch(`http://localhost:4000/books`)
       .then(resp=>resp.json())
       setBook(bookData)
       
    }
    fetchBookApi()
    let{id,title,pageCount,thumbnailUrl,shortDescription,longDescription,status,authors,categories}=book
  return (
    <>
    <div className="readbooks">
        <div className="container">
            <div className="img-side"></div>
            <div className="content-side"></div>
        </div>
    </div>
    </>
  )
}

export default ReadBooks
